<?php /*Plugin Name: greeklish-permalinksPlugin URI: https://github.com/elpak/Greeklish-permalink-wordpressDescription: Convert greek characters to latin (greeklish). Greek text turns into a long series of non-readable symbols when appeared in URLs, which makes it look bad. This plugin solves the problem and it just works! Tags: greek, greeklish permalink, latin, greeklish, slugs, permalinks, gr, autoconvertAuthor: Panagiotis KontogiannisVersion: 2.0Author URI: https://www.problogger.grRequires at least: 3.0Tested up to: 4.9.4License: GPLv2 or laterLicense URI: http://www.gnu.org/licenses/gpl-2.0.html*/// Based on Iacovos Constantinou pluginfunction greeklish_permalinks_sanitize_title($text) {	if ( !is_admin() ) return $text;		$expressions = array(		'/[αάΑΆ]/u'   => 'a',		'/[βΒ]/u'     => 'v',		'/[γΓ]/u'     => 'g',		'/[δΔ]/u'     => 'd',		'/[εέΕΈ]/u'   => 'e',		'/[ζΖ]/u'     => 'z',		'/[ηήΗΉ]/u'   => 'i',		'/[θΘ]/u'     => 'th',		'/[ιίϊΙΊΪ]/u' => 'i',		'/[κΚ]/u'     => 'k',		'/[λΛ]/u'     => 'l',		'/[μΜ]/u'     => 'm',		'/[νΝ]/u'     => 'n',		'/[ξΞ]/u'     => 'x',		'/[οόΟΌ]/u'   => 'o',		'/[πΠ]/u'     => 'p',		'/[ρΡ]/u'     => 'r',		'/[σςΣ]/u'    => 's',		'/[τΤ]/u'     => 't',		'/[υύϋΥΎΫ]/u' => 'y',		'/[φΦ]/iu'    => 'f',		'/[χΧ]/u'     => 'ch',		'/[ψΨ]/u'     => 'ps',		'/[ωώ]/iu'    => 'o',				'/[αΑ][ιίΙΊ]/u'                             => 'e',		'/[οΟΕε][ιίΙΊ]/u'                           => 'i',		'/[αΑ][υύΥΎ]([θΘκΚξΞπΠσςΣτTφΡχΧψΨ]|\s|$)/u' => 'af$1',		'/[αΑ][υύΥΎ]/u'                             => 'av',		'/[εΕ][υύΥΎ]([θΘκΚξΞπΠσςΣτTφΡχΧψΨ]|\s|$)/u' => 'ef$1',		'/[εΕ][υύΥΎ]/u'                             => 'ev',		'/[οΟ][υύΥΎ]/u'                             => 'ou',		'/(^|\s)[μΜ][πΠ]/u'                         => '$1b',		'/[μΜ][πΠ](\s|$)/u'                         => 'b$1',		'/[μΜ][πΠ]/u'                               => 'b',		'/[νΝ][τΤ]/u'                               => 'nt',		'/[τΤ][σΣ]/u'                               => 'ts',		'/[τΤ][ζΖ]/u'                               => 'tz',		'/[γΓ][γΓ]/u'                               => 'ng',		'/[γΓ][κΚ]/u'                               => 'gk',		'/[ηΗ][υΥ]([θΘκΚξΞπΠσςΣτTφΡχΧψΨ]|\s|$)/u'   => 'if$1',		'/[ηΗ][υΥ]/u'                               => 'iu',	);		$text = preg_replace( array_keys($expressions), array_values($expressions), $text );	return $text;}add_filter('sanitize_title', 'greeklish_permalinks_sanitize_title', 1);?>